import React, { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import { styled } from "styled-components"
import { ToastContainer, toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import Lottie from "react-lottie"
import * as dotLoading from "../assets/dot-loading.json"

import axios from "axios"
import { setAvatarRoute } from "../utils/APIroute"
import { Buffer } from "buffer"

function SetAvatar() {
  // State
  const [avatar, setAvatar] = useState([])
  const [selectedAvatar, setSelectedAvatar] = useState(0)
  const [loading, setLoading] = useState(true)

  const api = "https://api.multiavatar.com"
  const navigate = useNavigate()
  const option = {
    position: "bottom-right",
    pauseOnHover: true,
    draggable: true,
    theme: "dark",
  }

  const lottieOption = {
    loop: true,
    autoplay: true,
    animationData: dotLoading,
  }

  const setProfileAvatar = async () => {
    if (selectedAvatar < 0) {
      toast.error("Please select your avatar", {
        ...option,
        hideProgressBar: true,
        autoClose: 3000,
      })
    } else {
      const user = await JSON.parse(localStorage.getItem("spiel-user"))
      await axios
        .post(`${setAvatarRoute}/${user._id}`, {
          image: avatar[selectedAvatar],
        })
        .then((res) => {
          user.isAvatarImageSet = true
          user.avatarImage = res.data.image
          setLoading(true)
          localStorage.setItem("spiel-user", JSON.stringify(user))
          setTimeout(() => {
            setLoading(false)
            navigate("/")
          }, 1500)
        })
        .catch((error) => {
          toast.error("Error while save your avatar, Please try again.", option)
        })

      // if (data.isSet) {
      //   localStorage.setItem("spiel-user", JSON.stringify(user))
      //   setLoading(true)
      //   setTimeout(() => {
      //     setLoading(false)
      //     navigate("/")
      //   }, 1500)
      // } else {
      //   toast.error("Error while save your avatar, Please try again.", option)
      // }
    }
  }

  useEffect(() => {
    console.log(selectedAvatar)
  })

  useEffect(() => {
    const getAvatar = async () => {
      // const data = [
      //   "",
      //   "",
      //   "",
      //   "",
      // ]
      let data = []

      // api call 4 of avatar image
      for (let i = 0; i < 4; i++) {
        await axios
          .get(
            `${api}/${Math.floor(Math.random() * 10000)}?apikey=XT0rZxthwpOgRN`
          )
          // eslint-disable-next-line no-loop-func
          .then((res) => {
            const buffer = Buffer.from(res.data)
            data.push(buffer.toString("base64"))
          })
          // eslint-disable-next-line no-loop-func
          .catch(() => {
            toast.error(
              "Error while save your avatar, Please try again.",
              option
            )
            data = [
              "",
              "",
              "",
              "",
            ]
          })
      }

      setAvatar(data)
      setTimeout(() => {
        setLoading(false)
      }, 2000)
    }

    getAvatar()
  }, [])

  return (
    <>
      {loading ? (
        <Container>
          <Lottie options={lottieOption} height={400} width={400} />
        </Container>
      ) : (
        <Container>
          <div className="title-container">
            <h1>Pick your avatar</h1>
          </div>
          <div className="avatars">
            {avatar.map((img, idx) => (
              <div
                key={img + idx}
                className={`avatar ${
                  selectedAvatar === idx ? "selected" : null
                }`}
              >
                <img
                  src={`data:image/svg+xml;base64,${img}`}
                  alt="avatar"
                  onClick={() => setSelectedAvatar(idx)}
                />
              </div>
            ))}
          </div>
          <button className="summit-btn" onClick={() => setProfileAvatar()}>
            Set as a Proflie
          </button>
        </Container>
      )}

      <ToastContainer />
    </>
  )
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 3rem;
  background-color: #131324;
  width: 100vw;
  height: 100vh;
  .loader {
    max-inline-size: 100%;
  }

  .title-container {
    h1 {
      color: white;
    }
  }

  .summit-btn {
    display: inline-block;
    background-color: #997af0;
    color: white;
    padding: 1rem 2rem;
    border: none;
    font-weight: bold;
    cursor: pointer;
    border-radius: 0.4rem;
    font-size: 1rem;
    text-transform: uppercase;
    transition: 0.5s ease-in-out;
    &:hover {
      background-color: #4e0eff;
      /* text-decoration: underline; */
    }
  }

  .avatars {
    display: flex;
    gap: 2rem;
    .avatar {
      border: 0.4rem solid transparent;
      border-radius: 5rem;
      transition: 0.5s ease-in-out;
      display: flex;
      justify-content: center;
      align-items: center;
      padding: 0.4rem;
      img {
        height: 6rem;
        cursor: pointer;
      }
    }
    .selected {
      border: 0.4rem solid #4e0eff;
    }
  }
`

export default SetAvatar
